class BankingSystem():
    def __init__(self):
        self.accounts = {}  # key=acc_no, value=balance
        self.transactions = {}   # key=acc_no, value=list of transactions

    def create_account(self, acc_no, initial_deposit = 0):
        """Create a new account with the specified initial deposit."""
        if acc_no in self.accounts:
            raise ValueError("Account already exists.")

        self.accounts[acc_no] = initial_deposit
        self.transactions[acc_no] = []  # Initialize empty transaction list for this account

    def deposit(self, acc_no, amount):
        """Deposits the specified amount to the given account."""
        if acc_no not in self.accounts:
            raise ValueError("Account does not exist.")

        self.accounts[acc_no] += amount
        self.transactions[acc_no].append(('deposit', amount))  # Record this transaction

    def withdrawal(self, acc_no, amount):
        """Withdraw the specified amount from the given account."""
        if acc_no not in self.accounts:
            raise ValueError("Account does not exist.")

        if amount > self.accounts[acc_no]:
            raise ValueError("Insufficient balance")

        self.accounts[acc_no] -= amount
        self.transactions[acc_no].append(('withdrawal', amount))  # Record this transaction

    def calculate_interest(self, acc_no, rate):
        """Calculate and deposit interest on the specified account."""
        if acc_no not in self.accounts:
            raise ValueError("Account does not exist.")

        interest = self.accounts[acc_no] * (rate / 100)
        self.deposit(acc_no, interest)

    def get_balance(self, acc_no):
        """Return the current balance for a given account."""
        if acc_no not in self.accounts:
            raise ValueError("Account does not exist.")

        return self.accounts[acc_no]

    def get_transaction_history(self, acc_no):
        """Return the transaction history for a given account."""
        if acc_no not in self.transactions:
            raise ValueError("Account does not exist.")

        return self.transactions[acc_no]
``` 
